using System;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using SendGrid.Helpers.Mail;
using Utility.Extensions;
using Utility.Notifications;

namespace Web.API
{
    public class NotificationsController : BaseController
    {
        [HttpPost("sendMail")]
        public async Task<ActionResult> SendMail(SendMailObj sendMailObj)
        {
            var message = new EmailMessage();
            var monthYear = DateTime.Now.ToFormalMonthAndYear();
            var subject = "Monthly Payroll For " + monthYear;

            message.Subject = subject;

            subject = subject.Replace(", ", "_");

            /*message.Attachments.Add(new MailAttachment()
            {
                Content = "",
                FileName = "Monthly Payroll For " + monthYear + ".xlsx"
            });*/

            if (sendMailObj.AttachPdfContent)
            {
                message.Attachments.Add(new MailAttachment()
                {
                    Content = sendMailObj.PdfContent,
                    FileName = subject + ".pdf"
                });

            }

            if (sendMailObj.AttachExcelContent)
            {
                message.Attachments.Add(new MailAttachment()
                {
                    Content = sendMailObj.ExcelContent,
                    FileName = subject + ".xlsx"
                });

            }

            message.ReplyTo = new EmailAddress("abdulazeez.tobi@yahoo.com");
            message.ToRecipients.Add(new EmailAddress(sendMailObj.Recipient));
            message.CcRecipients.Add(new EmailAddress("bolarinwa346@gmail.com"));

            message.HtmlContent = "<p>Dear Banker, <br><br> Find attached Temile and Sons Employees' Pay Slip for " + monthYear +
                "<br><br>Best Regards, <br>Temile and Sons Limited</p>";

            message.PlainTextContent = "FYA";


            await Send.Mail(message);

            return Ok();
        }
    }

    public class SendMailObj
    {
        public string Recipient { get; set; }
        public bool AttachPdfContent { get; set; }
        public bool AttachExcelContent { get; set; }
        public string PdfContent { get; set; }
        public string ExcelContent { get; set; }
    }

}